INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (1, 0.01, 'Larue', 'Bia Larue lần đầu tiên được sản xuất vào năm 1909 bởi một nhà nấu bia người Pháp tên Victor Larue. Đây là sản phẩm của sự hòa quyện tinh tế. ', 0, 'https://down-vn.img.susercontent.com/file/vn-11134201-23030-rsxy6576z7nv59', 'nước, hoa bia, ngũ cốc, đại mạch, hương trái cây tổng hợp', 'Sản phẩm dành cho người trên 18 tuổi,Không dành cho phụ nữ mang thai.', 'Larue', 'Thùng 24 lon bia Larue 330ml/lon', 'Bảo quản nơi sạch sẽ, khô ráo, thoáng mát, tránh ánh nắng mặt trời.', 280000, 143, '2023-02-18', 1, 57);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (2, 0.05, 'Heniken', 'Thông tin sản phẩm:-Dung tích: 330ml/ lon.-Thành phần: nước, hoa bia, đại mạch.-Nồng độ cồn: 5%.Ngoài nước, đại mạch và hoa bia thì ‘thành phần’ đặc biệt và cũng là yếu tố chính tạo nên hương vị bia đặc biệt hoàn hảo của Heineken đó chính là men A-Yeast®. Với quá trình ủ bia trong bồn ngang trong 28 ngày nhằm mang đến áp suất hoàn hảo cho men Heineken A-Yeast® đạt ngưỡng tuyệt hảo, và tạo ra hương vị bia tuyệt hảo, cân bằng và êm dịu nhất. Ưu tiên hàng đầu của Heineken luôn rõ ràng: chất lượng tuyệt hảo đặt lên trên hiệu quả kinh tế.', 0, 'https://product.hstatic.net/200000078749/product/ia_heineken_silver_lon_loc_6x330ml-01_94f78d9409794e90acb904494f21fa11_7538b3a7b9d8489f8a29aaa1f86429c3.jpg', 'nước, hoa bia, men A, đại mạch,', 'Bia được uống trực tiếp trong lon hoặc đổ ra ly, ngon hơn khi uống lạnh, tốt nhất ở 10°C - 15°C', 'Heniken', 'Thùng 24 lon bia Heineken 330ml/lon', 'Bảo quản nơi sạch sẽ, khô ráo, thoáng mát, tránh ánh nắng mặt trời.', 485000, 0, '2023-01-18', 1, 70);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (3, 0.06, 'Huda', 'Huda là dòng bia biểu tượng của miền Trung Việt Nam.', 0, 'https://down-vn.img.susercontent.com/file/ee6826544815294707f0868cabe24f6e', 'Nước, đại mạch, gạo, hoa bia', 'Không dành cho người dưới 18 tuổi & phụ nữ có thai ,không dùng thức uống này khi tham gia giao thông', 'Huda', 'Thùng 24 lon bia Huda 330ml (330ml/lon)', 'Bảo quản nơi khô ráo và thoáng mát', 220000, 4, '2022-02-26', 1, 1541);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (4, 0, 'Sting', 'Được ra đời 2003, Sting - một sản phẩm nước tăng lực chất lượng của Tập đoàn PepsiCo.', 0, 'https://down-vn.img.susercontent.com/file/5833b374828c79add34a62087b93007b', ' Nước bão hòa CO2, đường mía, chất điều chỉnh độ a-xít (330, 331iii),', 'Dùng trực tiếp, ngon hơn khi ướp lạnh, hoặc dùng với đá', 'Sting', 'Thùng 24 Chai Nước Tăng Lực Sting Dâu (330ml/chai)', 'Bảo quản nơi sạch sẽ, khô ráo thoáng mát, tránh ánh nắng mặt trời.', 150000, 1, '2023-07-18', 1, 719);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (5, 0.03, 'Tiger', 'Tiger Crystal là dòng bia mới của Tiger Beer. Điểm độc đáo của bia Tiger Crystal là được sản xuất theo quy trình Cold Suspension. Từng dòng bia được tinh lọc với kỹ thuật làm lạnh sâu đến -1C (Crystal Cold), hoa bia được tinh chế đặc biệt giúp lưu giữ trọn vẹn hương vị tuyệt hảo của bia. Nhờ vậy, Tiger Crystal không chỉ đạt đến chất lượng hàng đầu thế giới như truyền thống của Tiger Beer mà còn mang đến cảm giác thật sảng khoái và rất dễ uống.', 0, 'https://down-vn.img.susercontent.com/file/sg-11134201-22120-87kv7dzjydlvae', 'Thành phần bia: Nước, đại mạch, ngũ cốc, hoa bia.', 'Thưởng thức có trách nhiệm, đã uống đồ uống có cồn thì không lái xe.', 'Viet Nam', 'Nồng độ cồn 4.6% - Thùng 24 lon bia Tiger Crystal 330ml/lon', 'Bảo quản nơi sạch sẽ, khô ráo, thoáng mát, tránh ánh nắng mặt trời.', 600000, 105, '2023-09-10', 1, 78);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (6, 0.02, 'Rivive', 'KHÔNG CHỈ MẤT NƯỚC, BẠN CÓ THỂ MẤT SỨC DO VẬN ĐỘNG. Đừng để cuộc vui gián đoạn vì mất nước, mất sức khi bạn đổ mồ hôi. ', 0, 'https://down-vn.img.susercontent.com/file/sg-11134201-22110-2wz8oclv70jv97', 'Nước bão hòa CO2, đường sucrose và dextrose, chất điều chỉnh độ axít (330)', 'Đã khát và sảng khoái, bù nước và bù sức nhanh, cho bạn nhanh chóng trở lại cuộc vui năng động', 'Viet Nam', 'Thùng 24 Chai Nước Giải Khát Revive (500ml/chai)', 'Bảo quản nơi khô mát, tránh nắng, tránh mùi mạnh và hoá chất', 185000, 75, '2023-03-11', 1, 31);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (7, 0, 'TEA+ Plus', 'Trà Ô Long TEA+ Plus là sản phẩm mang thương hiệu Suntory đầu tiên được ra mắt tại thị trường Việt Nam từ tháng 8 năm 2013. Được sản xuất trên quy trình công nghệ hiện đại, thông qua quy trình kiểm duyệt chặt chẽ, không có đường hóa học, không chứa hóa chất độc hại, đảm bảo an toàn cho người sử dụng.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-23010-ge67tdyrgamvbe', 'Nước, đường, trà ô long 7,52 g/1 (OTPP được trích ly tối thiểu 155mg/l), hương giống tự nhiên, chất chống oxy hóa (300), chất điều chỉnh độ axit (500ii)', ' Dùng trực tiếp, ngon hơn khi ướp lạnh, hoặc dùng với đá', 'Viet Nam', 'Thùng 24 Chai Trà Ô long Tea+ Plus (320ml/Chai)', 'Bảo quản nơi sạch sẽ, khô ráo thoáng mát, tránh ánh nắng mặt trời.', 165000, 32, '2023-01-06', 1, 23);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (8, 0.15, 'Trà Không Độ', 'Sản phẩm được sản xuất dựa trên dây chuyền công nghệ hiện đại Nhật Bản, hoàn toàn khép kín và khử trùng cao nên đảm bảo an toàn sức khỏe cho người sử dụng.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7qukw-lfpmp3woqnb8ce', 'Nước, đường, fructose, trà xanh, vitamin C, hương chanh tự nhiên, chất điều chỉnh độ chua.', 'Dùng trực tiếp, ngon hơn khi ướp lạnh, hoặc dùng với đá', 'Tân Hiêp Phát', '1 Lốc ( 6 Chai ) Trà Xanh Không Độ 500ml', 'Bảo quản nơi khô mát, tránh nắng, tránh mùi mạnh và hoá chất', 188000, 91, '2022-10-18', 1, 106);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (9, 0, 'Coca Cola', 'Lốc 6 Lon Nước Giải Khát Coca-Cola vị Nguyên Bản Original Lon 320ml là loại nước ngọt được nhiều người yêu thích với hương vị thơm ngon, sảng khoái. Lốc 6 lon nước ngọt Coca Cola 320ml chính hãng nước ngọt Coca Cola với lượng gas lớn sẽ giúp bạn xua tan mọi cảm giác mệt mỏi, căng thẳng, đem lại cảm giác thoải mái sau khi hoạt động ngoài trời.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-ll2zyr55sr8dd7', 'Các sản phẩm nước ngọt Coca cola có thành phần chính bao gồm: Nước bão hòa CO2, đường HFCS, đường mía, màu tự nhiên (caramen nhóm IV), chất điều chỉnh độ acid, hương cola tự nhiên và caffeine.', 'Ngon hơn khi uống lạnh', 'Cocola', 'Coca-Cola vị Nguyên Bản Original Lon 320ml', 'Bảo quản nước ngọt ở nơi khô ráo thoáng mát, tránh ánh nắng trực tiếp', 180000, 42, '2023-08-29', 1, 19);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (10, 0.06, 'Vinamilk', 'Thùng sữa Vinamilk bịch loại có đường 220 ml được sản xuất trong quy trình khép kín, kiểm tra chặt chẽ và an toàn.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-ll3ocrad3umg4d', 'Thành phần Sữa bò tươi (95,9%), đường tinh luyện (3,8%), chất ốn đinh (471, 460(i), 407,466), vitamin (natri ascorbat, A, D3), khoáng chất (natri selenit), hương liệu tổng hợp dùng cho thực phẩm', 'Uống sữa trong những lúc cơ thể cần năng lượng như xế trưa, xế chiều cũng được khuyến khích vì nó giúp bổ sung nguồn dinh dưỡng mà chúng ta đang thiếu hụt.', 'Vinamilk', 'Lốc Sữa Vinamiulk 100% 180ml', 'Vì sữa thanh trùng cần được bảo quản kỹ càng hơn nên phù hợp khi sử dụng ở nhà hoặc ở môi trường có tủ lạnh. Ngược lại, sữa tươi tiệt trùng có thể sử dụng mọi lúc mọi nơi như lúc đi học, đi làm hay đi chơi.', 245000, 188, '2022-03-12', 1, 164);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (11, 0.02, 'TH Milk', 'Dòng sữa TH true MILK ít đường lấy nguồn sữa từ đàn bò được nuôi ở trang trại tươi sạch và luôn ứng dụng các công nghệ tiên tiến trên thế giới vào quy trình sản xuất của nhà TH.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7qukw-ljo8n24ctj4267', 'Sữa hoàn toàn từ sữa bò tươi (96%), đường (3,8%), hỗn hợp chất nhũ hóa và chất ổn định (E471, E407, E412)', 'Sản phẩm cho 1 lần uống,lắc đều trước khi sử dụng.lắc đều trước khi sử dụng,ngon hơn khi uống lạnh,ngon hơn khi uống lạnh.', 'TH Milk', 'Thùng 48 hộp sữa tươi tiệt trùng TH true MILK ít đường 110 ml (từ 1 tuổi)', 'Bảo quản nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp.', 285000, 147, '2023-02-10', 1, 83);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (12, 0, 'Ensure', 'Abbott là thương hiệu sữa thuộc công ty Abbot Nutrition của Mỹ, chuyên sản phẩm dinh dưỡng dành cho trẻ em, người lớn tuổi, là một trong những thương hiệu được tin cậy nhất trên thế giới', 0, 'https://down-vn.img.susercontent.com/file/sg-11134201-23010-ljtuqc4nixlv14', 'Tinh bột bắp thuỷ phân, sucrose, DẦU THỰC VẬT (dầu hướng dương giàu oleic, dầu đậu nành, dầu hạt cải', 'Không chứa Gluten và Lactose. Không dùng cho người bệnh galactosemia. Không dùng qua đường tĩnh mạch.', 'Abot', 'Thùng 24 chai Ensure nước Original Vani 237ml date 9/2024', 'Ở nhiệt độ phòng. Khi đã mở, phải sử dụng ngay, phần chưa sử dụng phải đậy kín, cho vào tủ lạnh và dùng trong vòng 24 giờ', 970000, 49, '2023-06-29', 1, 61);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (13, 0.02, 'Bánh Oreo', 'Đây được xem là thế hệ kế tiếp của Orion đã cho ra đời một Thanh Oreo Wahas Stick Choco thơm ngon, hấp dẫn tạo nên một sự khác lạ thu hút người tiêu dùng.Thanh Oreo Wahas Stick White có dạng thanh với lớp kem chocolate bên ngoài chỉ cần cắn một cái bạn sẽ có thể cảm nhận ngay lập tức vị vanili ngọt thơm mềmSản phẩm được chế biến từ nguyên liệu chất lượng thông qua lựa chọn kỹ càng, đảm bảo mang đến người tiêu dùng những chiếc bánh chất lượng, đảm bảo an toàn vệ sinh thực phẩm.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-ll7ql8k5ob0od5', 'Năng lượng: 67 kcal.Chất béo: 2.8g.Protein: 0.6g.Độ bão hòa chất béo: 1.4g.Chất xơ: 0.3g.Sodium (natri): 71mg.Cholesterol: 0mg.Gluxit: 9.8g.', 'Ăn trực tiếp', 'Mỹ', 'Bánh Oreo Pie Blackpink 360g vị marshmallow', 'bảo quản nơi khô ráo, tránh ánh nắng trực tiếp', 46000, 60, '2023-06-18', 2, 8);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (14, 0.01, 'Bánh Kinh Đô', 'Với mỗi người Việt Nam, Tết Trung Thu là dịp quý giá để người lớn chúng ta cùng về bên gia đình. Là dịp để trẻ con thóa thích rước đèn vui đùa cùng chúng bạn dưới ánh trăng rằm sáng trong, là dịp để thắt chặt tình bằng hữu, nghĩa tâm giao khi ta cẩn thận chọn từng hộp bánh đẹp nhất, ngon nhất tặng bạn bè, người thân, đối tác ... Và bánh trung thu là món bánh không thể thiếu trong những phút giây đong đầy tinh thần đó.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134201-7r98o-llf33sdm3ao847', 'nguyên liệu thượng hạng mà còn là sự tinh tuyển khắt khe cho từng trái trứng muối, từng hạt đậu, hạt sen ... ', 'Ăn trực tiếp', 'Kinh đô', '"Team Hạt Sen Đậu Đỏ" - Hộp 2 bánh trung thu Kinh Đô', 'bảo quản nơi khô ráo, tránh ánh nắng trực tiếp', 140000, 147, '2023-09-09', 2, 23);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (15, 0, 'Aquafina', 'Aquafina là nhãn hiệu nước uống hàng đầu Việt Nam với hành trình lan tỏa câu chuyện đầy cảm hứng về vị ngon của sự tinh khiết của nước. Sản phẩm được xử lý qua quy trình lọc Hydro – 7 , quy trình này bao gồm công nghệ thẩm thấu ngược cùng một số bước lọc khác nhằm loạt bỏ triệt để khoáng, vi khuẩn và các tạp chất trong nước. Nhờ đó, tạo nên sản phẩm nước tinh khiết hoàn hảo với hương vị tươi mới hoàn toàn khác biệt.', 0, 'https://nuocaquafina.com.vn/wp-content/uploads/2019/07/Thung-Aquafina-355ml-1-1.jpeg', 'Công thức i-Hydr8 độc đáo, ưu việt, bổ sung muối khoáng (Na+, K+) và các vitamin nhóm B (B3, B6, B12), giúp bù nước vượt trội, bù khoáng và bù đắp năng lượng, cho bạn trở lại cuộc vui năng động.', 'Uống trực tiếp', 'Việt Nam', '1 Thùng 24 Chai Revive và 1 Thùng 24 Aquafina (500ml/chai)', ' Bạn chỉ cần để ở nơi khô ráo và thoáng mát, tránh nơi có nhiệt độ cao và đặc biệt không để tiếp xúc trực tiếp với ánh nắng mặt trời. Khi chưa sử dụng hết, cần đóng nắp kín và bảo quản ở nhiệt độ thường hoặc tủ mát có nhiệt độ từ 5 -10 độ C', 320000, 66, '2022-09-08', 1, 84);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (16, 0.03, 'LAVIE', 'Nước chiếm tới 75% trong cơ thể con người và đóng vai trò hết sức quan trọng trong việc đảm bảo cho cơ thể hoạt động hiệu quả và khỏe mạnh. Cơ thể con người không thể thiếu nước. Việc Uống 8 – 10 ly nước mỗi ngày sẽ giúp cơ thể hoạt động hiệu quả hơn gấp 30%. Sự mất nước của da sẽ khiến da bị khô hoặc có thể xuất hiện thêm nhiều nếp nhăn trên da. Nước khoáng Lavie bổ sung oxy cho cơ bắp của bạn, hỗ trợ cơ bắp hoạt động hiệu quả và bền bỉ. Nước khiến dạ dày cảm thấy đầy do lượng calo từ nước bạn uống vì thế bạn sẽ cảm thấy no lâu hơn. Khi muốn giảm cân bạn hãy uống nhiều nước hơn bình thường nhé! Thành Phần có trong nước khoáng Lavie', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7qukw-lh7hefyf0hrn8e', 'Công thức i-Hydr8 độc đáo, ưu việt, bổ sung muối khoáng (Na+, K+) và các vitamin nhóm B (B3, B6, B12), giúp bù nước vượt trội, bù khoáng và bù đắp năng lượng, cho bạn trở lại cuộc vui năng động.', 'Uống trực tiếp', 'Việt Nam', 'Thùng Lavie 500ml ( 24 chai )', 'Bạn chỉ cần để ở nơi khô ráo và thoáng mát, tránh nơi có nhiệt độ cao và đặc biệt không để tiếp xúc trực tiếp với ánh nắng mặt trời. Khi chưa sử dụng hết, cần đóng nắp kín và bảo quản ở nhiệt độ thường hoặc tủ mát có nhiệt độ từ 5 -10 độ C', 100000, 56, '2022-09-15', 1, 14);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (17, 0.05, 'AFC', 'Mondelez International biết rằng ăn vặt là một phần thiết yếu trong thói quen hàng ngày của bạn và vì vậy sứ mệnh của chúng tôi là nâng cấp trải nghiệm ăn vặt của bạn hơn bao giờ hết. Đa dạng các món ngọt, chua, cay, nóng được tuyển chọn cẩn thận của chúng tôi đảm bảo rằng bạn luôn có thứ gì đó ngon miệng để ăn bất kỳ lúc nào. Giờ đây, hãy vui vẻ thưởng thức khoai tây chiên, socola, bánh quy và các món ăn nhẹ khác của chúng tôi được làm một cách có ý thức bằng cách sử dụng nguyên liệu bền vững nhất.
