INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (21, 0.05, 'Bánh Danisa', 'Cung cấp nhiều dưỡng chất thiết yếu cho cơ thể, đặc biệt là các chất hữu ích như canxi làm chắc xương, chất sắt giúp phát triển trí tuệ, chất xơ hỗ trợ hệ tiêu hóa. Sản phẩm giòn, hình dạng theo khuôn, không vỡ vụn, không lẫn tạp chất lạ. Màu vàng đặc trưng, mùi thơm, vị ngọt đặc trưng .
