INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (23, 0, 'Bánh MonCake', 'Thương hiệu Bảo Ngọc được ra đời từ năm 1986 với tiền thân là một tiệm bánh ngọt đến năm 1989 thì phát triển thành cơ sở sản xuất bánh ngọt Bảo Ngọc. Năm 2012 chuyển thành công ty cổ phần đầu tư sản xuất Bảo Ngọc, năm 2021 chính thức niêm yết cổ phiểu trên sàn giao dịch chứng khoán Hà Nội. Với sứ mệnh sản xuất thực phẩm có hàm lượng dinh dưỡng cao Bảo Ngọc có tầm nhìn trở thành tập đoàn đa quốc gia với nhiều công ty thành viên hoạt động trong lĩnh vực nuôi trồng chế biến thực phẩm, đồ uống...', 0, 'https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-llh86ljqm0tyd0', 'Thành phần nguyên liệu có chất lượng hảo hạng như bột mì, đường, dầu dừa, cơm dừa sấy, hạt mè, sữa bột nguyên kem, muối, bột phô mai… ', 'Ăn trực tiếp', 'Việt Nam', 'Thùng bánh trung thu Bảo Ngọc trăng hàn hộp 2 bánh BN01,BD06 Thùng 4 bánh BN31,BN51,BD07,BD08', 'Bảo quản hoặc trưng bày ở nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp.', 450000, 80, '2023-08-19', 2, 100);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (24, 0.08, 'Bánh Kinh Đô', 'Tiếp nối sự thành công của mọi năm, năm nay Andanfood lại được vinh dự trở thành Đại lý Top 1 của Kinh Đô Việt Nam.
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (25, 0, 'CLEAR', 'SẠCH GÀU KHÔNG KHÓ, CÓ CLEAR MÁT LẠNH BẠC HÀ Thời tiết nóng ẩm cùng khói bụi là nguyên nhân chính gây ra gàu, khiến bạn mất tự tin. Đã có Dầu gội Sạch Gàu CLEAR Mát Lạnh Bạc Hà với công thức 3X Sức mạnh giúp bạn đánh bay gàu. ngứa và vi khuẩn.Với khả năng thẩm thấm nhanh và sâu trong da đầu nhờ vitamin B3 cùng glycerin, Dầu gội Sạch Gàu CLEAR Mát Lạnh Bạc Hà nuôi dưỡng da đầu hiệu quả, sạch gàu, không gây cảm giác khô cho da đầu.Đặc biệt hơn, Dầu gội Sạch Gàu CLEAR Mát Lạnh Bạc Hà đem lại cảm giác mát lạnh cùng hương thơm tươi mát ngay lần đầu sử dụng với hiệu quả sạch gàu, nhờn với hiệu quả trông thấy.', 0, 'https://down-vn.img.susercontent.com/file/2f28b4711145ced4fb65df938899d121', 'Vitamin B3 và Glycerin kết hợp tạo nên một lớp bảo vệ hoàn hảo cho tóc khỏi khói bụi và thời tiết ẩm.', 'Cho một lượng nhỏ vào lòng bàn tay,Dùng bàn tay xoa nhẹ nhàng lên đầu,Bước 3: Gội sạch với nước.', 'Anh', 'Dầu Gội Clear Sạch Gàu 1.4kg Mát Lạnh Bạc Hà 3x Sức Mạnh Đánh Bay Gàu, Ngứa, Vi Khuẩn', 'Nơi thoáng mát, tránh ánh nắng trực tiếp', 800000, 20, '2021-09-17', 3, 20);
