INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (27, 0.08, 'X-MEN', 'Thông tin thương hiệu: Nerman là thương hiệu mỹ phẩm dành riêng cho nam giới được xây dựng dựa trên những phản hồi tích cực của khách hàng và những kiến thức chăm sóc da khoa học, điều đó đã giúp duy trì những điều cốt lõi trong sản phẩm cũng như sự phát triển của chúng tôi. Chúng tôi hiểu rằng sự cần thiết của việc chăm sóc da và chúng tôi sẽ giữ được lời hứa của mình. Bạn sẽ hoàn toàn cảm thấy yên tâm khi sử dụng và mua chúng.', 0, 'https://down-vn.img.susercontent.com/file/vn-11134201-7qukw-lfxbq6w9z2p631', ' Water, sodium laureth sulfate, cocamidopropyl betaine, dimethicone, glycerin, glycol distearate, PEG-7 glyceryl cocoate, perfume, sodium lactate,... ', 'Làm ướt tóc.Lấy một lượng dầu gội vừa đủ, thoa đều lên tóc ướt. Xoa bóp nhẹ nhàng và gội sạch với nước.Gội thôi chưa đủ, để phát huy hiệu quả vượt trội của dầu gội.', 'Việt Nam', 'Sữa tắm gội hương nước hoa cao cấp 350ml & Gel vệ sinh nam 100ml', 'Nơi thoáng mát, tránh ánh nắng trực tiếp', 850000, 40, '2022-02-29', 3, 50);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (28, 0, 'Yến Dinh Dưỡng', 'Các ngự y thời xưa tin rằng cũng như khoa học ngày nay đã chứng minh: Tổ yến là món ăn giúp bồi bổ trí lực, cải thiện vóc dáng và có khả năng cải lão hoàn đồng, giữ mãi nét thanh xuân. Thưởng thức Tổ yến cũng là một nét ẩm thực hết sức tao nhã và tinh tế: một chút thanh mát, nhẹ nhàng hòa quyện cùng các nguyên liệu bổ dưỡng khác tạo nên món ăn khó quên.', 0, 'https://bizweb.dktcdn.net/100/344/969/products/ngu-coc-2.jpg?v=1681210156773', 'Bột kem không sữa (11g/25g), vảy ngũ cốc (8g/25g) (bao gồm lúa mì, bắp, gạo, chiết xuất lúa mạch), đường tinh luyện, muối I-ốt, canxi cacbonat, tổ yến sấy thăng hoa (25mg/25g), các vitamin (D3, K2, B1, B2, B6, B12).', 'Không dùng cho trẻ em dưới 3 tuổi. Không dùng sản phẩm hết hạn và có hiện tượng lạ. Không dùng cho người dị ứng với các thành phần trong sản phẩm.', 'Việt Nam', 'NGŨ CỐC YẾN DINH DƯỠNG THÙNG 10 GÓI', 'Để sản phẩm nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp.', 600000, 100, '2023-09-06', 5, 60);
INSERT INTO product_sprint_two.products (id, bonus_sale, brand_product, detail_product, flag_product, img_product, ingredient_product, instruction_product, manufacture_product, name_product, preservation_product, price_product, quality_product, release_date, type_product_id, total_sold) VALUES (29, 0.03, 'Cháo Yến ', 'Cháo ăn liền là thực phẩm vừa tiện dụng, hợp vệ sinh và còn bổ sung nhiều dinh dưỡng cần thiết. Hiểu được những lợi ích mà thực phẩm này mang đến, Khánh Hòa NutriKidgrow đã cho ra đời sản phẩm Cháo Dinh Dưỡng Tổ Yến Cao Cấp Vị Cá Hồi & Tôm Bổ Sung Vitamin K2 sử dụng ba nguồn nguyên liệu chính là tổ yến vị thịt tôm, cá hồi và rau củ quả bổ sung nhiều chất dinh dưỡng cần thiết cho cơ thể.
